mapper.insert:
DO $$
DECLARE animalId NUMERIC;
BEGIN
INSERT INTO animal (name, describe, age, id_animaltype, isDeleted, createDate)
VALUES (#{animal.name}, #{animal.describe}, #{animal.age}, (SELECT id FROM animalType WHERE animalType.animalType = #{animal.typeOfAnimal}),
false, CURRENT_DATE)
RETURNING id INTO animalId;
INSERT INTO bird (id, wings) VALUES (animalId, #{wings});
INSERT INTO bullfinch(id, color) VALUES (animalId, #{color});
END $$;

mapper.update:
UPDATE crab SET taste = #{animal.taste} WHERE ID = #{animal.id};
UPDATE crustacean SET seashell = #{animal.seashell} WHERE ID = #{animal.id};
UPDATE animal SET name = #{animal.name} ,describe = #{animal.describe}, age = #{animal.age} WHERE ID = #{animal.id} AND isdeleted = #{animal.isDeleted};

mapper.delete:
DELETE FROM crab WHERE id = #{id};
DELETE FROM crustacean WHERE id = #{id};
DELETE FROM animal WHERE id = #{id};
