mapper.update:
UPDATE squirrel SET fur = #{animal.fur} WHERE ID = #{animal.id};
UPDATE mammal SET tail = #{animal.tail} WHERE ID = #{animal.id};
UPDATE animal SET name = #{animal.name} ,describe = #{animal.describe}, age = #{animal.age} WHERE ID = #{animal.id} AND isdeleted = #{animal.isDeleted};

mapper.delete:
DELETE FROM squirrel WHERE id = #{id};
DELETE FROM mammal WHERE id = #{id};
DELETE FROM animal WHERE id = #{id};
